class Perawat extends CI_Controller{
    function __construct()
    {
        parent::__construct();
        $this->load->model('M_perawat_model');

        
        if(!$this->session->userdata('islogin')) {

            redirect('dashboard');

        }
    } 

    /*
     * Listing of m_perawat
     */
    function index()
    {
        $data['m_perawat'] = $this->M_perawat_model->get_all_m_perawat();

        $data['_view'] = 'perawat/index';
        $this->load->view('layouts/main',$data);
    }

    /*
     * Adding a new m_perawat
     */
    function add()
    {   
        $this->load->library('form_validation');

		$this->form_validation->set_rules('nomor_telepon','Nomor Telepon','required');
		$this->form_validation->set_rules('nama','Nama','required');
		$this->form_validation->set_rules('email','Email','required');
		$this->form_validation->set_rules('password','Password','required');
		
		if($this->form_validation->run())     
        {   
            $params = array(
				'password' => $this->input->post('password'),
				'nomor_registrasi' => $this->input->post('nomor_registrasi'),
				'tanggal_registrasi' => $this->input->post('tanggal_registrasi'),
				'nomor_telepon' => $this->input->post('nomor_telepon'),
				'nama' => $this->input->post('nama'),
				'alamat' => $this->input->post('alamat'),
				'kecamatan' => $this->input->post('kecamatan'),
				'kelurahan' => $this->input->post('kelurahan'),
				'kota' => $this->input->post('kota'),
				'provinsi' => $this->input->post('provinsi'),
				'golongan_darah' => $this->input->post('golongan_darah'),
				'usia' => $this->input->post('usia'),
				'username' => $this->input->post('username'),
				'email' => $this->input->post('email'),
				'status_login' => 'perawat',
                'approved' => '1',
            );
            
            $m_perawat_id = $this->M_perawat_model->add_m_perawat($params);
            redirect('perawat/index');
        }
        else
        {            
            $data['_view'] = 'perawat/add';
            $this->load->view('layouts/main',$data);
        }
    }  

    /*
     * Editing a m_perawat
     */
    function edit($pk)
    {   
        // check if the m_perawat exists before trying to edit it
        $data['m_perawat'] = $this->M_perawat_model->get_m_perawat($pk);
        
        if(isset($data['m_perawat']['pk']))
        {
            $this->load->library('form_validation');

			$this->form_validation->set_rules('nomor_telepon','Nomor Telepon','required');
			$this->form_validation->set_rules('nama','Nama','required');
			$this->form_validation->set_rules('email','Email','required');
			$this->form_validation->set_rules('password','Password','required');
		
			if($this->form_validation->run())     
            {   
                $params = array(
					'password' => $this->input->post('password'),
					'nomor_registrasi' => $this->input->post('nomor_registrasi'),
					'tanggal_registrasi' => $this->input->post('tanggal_registrasi'),
					'nomor_telepon' => $this->input->post('nomor_telepon'),
					'nama' => $this->input->post('nama'),
					'alamat' => $this->input->post('alamat'),
					'kecamatan' => $this->input->post('kecamatan'),
					'kelurahan' => $this->input->post('kelurahan'),
					'kota' => $this->input->post('kota'),
					'provinsi' => $this->input->post('provinsi'),
					'golongan_darah' => $this->input->post('golongan_darah'),
					'usia' => $this->input->post('usia'),
					'username' => $this->input->post('username'),
					'email' => $this->input->post('email'),
					'status_login' => 'perawat',
                    'approved' => '1',
                );

                $this->M_perawat_model->update_m_perawat($pk,$params);            
                redirect('perawat/index');
            }
            else
            {
                $data['_view'] = 'perawat/edit';
                $this->load->view('layouts/main',$data);
            }
        }
        else
            show_error('The m_perawat you are trying to edit does not exist.');
    } 

    /*
     * Deleting m_perawat
     */
    function remove($pk)
    {   
        $this->M_cek_hapus->cek('m_jadwal_praktek','perawat_fk',$pk,'perawat');

        $m_perawat = $this->M_perawat_model->get_m_perawat($pk);

        // check if the m_perawat exists before trying to delete it
        if(isset($m_perawat['pk']))
        {
            $this->M_perawat_model->delete_m_perawat($pk);
            redirect('perawat/index');
        }
        else
            show_error('The m_perawat you are trying to delete does not exist.');
    }

    public function cek_nomor_registrasi(){
        $nomor_registrasi = $this->input->post('nomor_registrasi');
        $query = $this->M_perawat_model->cek_nomor_registrasi($nomor_registrasi);
        $status           = "true";
        if($query){ $status = "false"; }
            echo $status;
    }

     public function cek_nomor_registrasi_edit($pk){
        $nomor_registrasi = $this->input->post('nomor_registrasi_edit');
        $query = $this->M_perawat_model->cek_nomor_registrasi_edit($nomor_registrasi,$pk);
        $status           = "true";
        if($query){ $status = "false"; }
            echo $status;
    }
    
    public function cek_username(){
        $username = $this->input->post('username');
        $query = $this->M_perawat_model->cek_username($username);
        $status           = "true";
        if($query){ $status = "false"; }
            echo $status;
    }

    public function cek_username_edit($pk){
        $username = $this->input->post('username_edit');
        $query = $this->M_perawat_model->cek_username_edit($username,$pk);
        $status           = "true";
        if($query){ $status = "false"; }
            echo $status;
    }
}
